SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION hd_obter_ult_dados_periton ( cd_pessoa_fisica_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Busca as ultimas informacoes inseridas na tabela HD_DIALISE_PERITONIAL utilize
ie_opcao_p para assumir os valores('P','O','K') onde P = PET, O = Orificio e K = KTV
-------------------------------------------------------------------------------------------------
Locais de chamada direta: 
[ X]  Objetos do dicionario [  ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ ] Outros:
 ------------------------------------------------------------------------------------------------
Pontos de atencao:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w	varchar(255);


BEGIN

    if (ie_opcao_p = 'P') then

        begin
            select * into STRICT ds_retorno_w
            from (SELECT b.ds_pet
                    from	HD_DIALISE_PERITONIAL a,
                                    hd_cadastro_pet b
                    where cd_pessoa_fisica = cd_pessoa_fisica_p
                    and       a.nr_seq_pet = b.nr_sequencia
                    order by trunc(dt_dialise_peritonial) desc,
                    to_char(a.hr_dialise_peritonial,'HH24:MI:SS') desc) alias3 LIMIT 1;
        exception when others then
            ds_retorno_w :=  null;
        end;

    elsif (ie_opcao_p = 'O') then

        begin
            select * into STRICT ds_retorno_w
            from (SELECT  b.ds_orificio
                    FROM	HD_DIALISE_PERITONIAL a,
                                    hd_orificio b
                    WHERE cd_pessoa_fisica = cd_pessoa_fisica_p
                    and       a.nr_seq_orificio = b.nr_sequencia
                    order by trunc(dt_dialise_peritonial) desc,
                    to_char(a.hr_dialise_peritonial,'HH24:MI:SS') desc) alias3 LIMIT 1;
        exception when others then
            ds_retorno_w :=  null;
        end;

    elsif (ie_opcao_p = 'K') then

        begin
            select * into STRICT ds_retorno_w
                from (SELECT qt_ktv
                    from	HD_DIALISE_PERITONIAL
                    where cd_pessoa_fisica = cd_pessoa_fisica_p
                    order by trunc(dt_dialise_peritonial) desc,
                    to_char(hr_dialise_peritonial,'HH24:MI:SS') desc) alias3 LIMIT 1;
        exception when others then
            ds_retorno_w :=  null;
        end;

    end if;

    return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
