SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION hd_obter_ult_dialisador ( cd_pessoa_fisica_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*ie_opcao_p

'C'	= código
'D'	= descrição
'M'	= modelo do dialisador
'Q'	= reusos
'P'	= data de priming
*/
nr_seq_dialise_dialisador_w	bigint;
nr_seq_dialisador_w		bigint;
ds_dialisador_w			varchar(80);

ds_retorno_w			varchar(80);
ds_modelo_w			varchar(80);
qt_reuso_w			bigint;
dt_prim_uso_w			timestamp;


BEGIN

select	coalesce(max(a.nr_sequencia),0)
into STRICT	nr_seq_dialise_dialisador_w
from	hd_dialise_dialisador a,
	hd_dialise b
where	cd_pessoa_fisica	= cd_pessoa_fisica_p
and	b.nr_sequencia		= a.nr_seq_dialise;

select	coalesce(max(nr_seq_dialisador),0)
into STRICT	nr_seq_dialisador_w
from	hd_dialise_dialisador
where	nr_sequencia = nr_seq_dialise_dialisador_w;


select	substr(obter_descricao_padrao('HD_MODELO_DIALISADOR','DS_MODELO', nr_seq_modelo),1,200)
into STRICT	ds_dialisador_w
from	hd_dializador
where	nr_sequencia	= nr_seq_dialisador_w;

select 	max(substr(obter_descricao_padrao('HD_MODELO_DIALISADOR','DS_MODELO', nr_seq_modelo),1,200)),
	max(qt_reuso),
	max(dt_prim_uso)
into STRICT	ds_modelo_w,
	qt_reuso_w,
	dt_prim_uso_w
from	hd_dializador
where	nr_sequencia	= nr_seq_dialisador_w;



if (ie_opcao_p = 'C') then
	ds_retorno_w 	:= nr_seq_dialisador_w;
elsif (ie_opcao_p = 'M') then
	ds_retorno_w	:= ds_modelo_w;
elsif (ie_opcao_p = 'Q') then
	ds_retorno_w	:= qt_reuso_w;
elsif (ie_opcao_p = 'P') then
	ds_retorno_w	:= to_char(dt_prim_uso_w,'dd/mm/yyyy');
else
	ds_retorno_w := ds_dialisador_w;
end if;



return	ds_retorno_w;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
