CREATE OR REPLACE FUNCTION hd_obter_ult_prescr_gerada (nr_seq_prot_exame_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_prot_prescr_w	bigint;
nr_prescricao_w		bigint;


BEGIN

select 	coalesce(max(nr_sequencia),0)
into STRICT 	nr_seq_prot_prescr_w
from	HD_PAC_PROT_EXAME_PRESCR
where 	nr_seq_prot_exame = nr_seq_prot_exame_p;

if (nr_seq_prot_prescr_w > 0) then
	select	max(nr_prescricao)
	into STRICT	nr_prescricao_w
	from	prescr_medica
	where	nr_prescricao = (SELECT 	max(nr_prescricao)
				from	HD_PAC_PROT_EXAME_PRESCR
				where	nr_sequencia = nr_seq_prot_prescr_w)
	and	coalesce(dt_suspensao::text, '') = '';

end if;

return	nr_prescricao_w;

end;
$body$
