SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION hd_obter_unid_dialise_data (cd_pessoa_fisica_p text, dt_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE


nr_seq_unid_dialise_origem_w	bigint;
nr_seq_unid_dialise_atual_w	bigint;
nr_seq_unid_dialise_w		bigint;
nr_seq_escala_dialise_w		bigint;


BEGIN

select  max(nr_seq_unid_dialise)
into STRICT	nr_seq_unid_dialise_origem_w
from	hd_pac_renal_cronico
where	cd_pessoa_fisica = cd_pessoa_fisica_p;

select 	max(nr_sequencia)
into STRICT	nr_seq_escala_dialise_w
from   	hd_escala_dialise
where  	dt_inicio <= dt_referencia_p
and	cd_pessoa_fisica = cd_pessoa_fisica_p
and	coalesce(dt_fim,dt_referencia_p) >= dt_referencia_p;

nr_seq_unid_dialise_atual_w	:= null;

if (nr_seq_escala_dialise_w > 0) then

	select 	nr_seq_unid_dialise
	into STRICT	nr_seq_unid_dialise_atual_w
	from   	hd_escala_dialise
	where	nr_sequencia = nr_seq_escala_dialise_w;


end if;

nr_seq_unid_dialise_w	:= nr_seq_unid_dialise_origem_w;

if (nr_seq_unid_dialise_atual_w IS NOT NULL AND nr_seq_unid_dialise_atual_w::text <> '') then
	nr_seq_unid_dialise_w	:= nr_seq_unid_dialise_atual_w;
end if;

return nr_seq_unid_dialise_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
