CREATE OR REPLACE FUNCTION hd_obter_unid_escala ( CD_PESSOA_FISICA_P text) RETURNS bigint AS $body$
DECLARE


vl_retorno_w		bigint;
/*
Retorna a unidade da Escala com a data de in√≠cio mais recente.
 */
BEGIN
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	SELECT 	max(nr_seq_unid_dialise)
	INTO STRICT	vl_retorno_w
	FROM 	hd_escala_dialise
	WHERE 	dt_inicio = (SELECT MAX(dt_inicio)
			 FROM 	HD_ESCALA_DIALISE
			 WHERE 	cd_pessoa_fisica = cd_pessoa_fisica_p
			 AND 	coalesce(dt_fim::text, '') = '');

end if;

RETURN vl_retorno_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
