CREATE OR REPLACE FUNCTION hd_obter_valor_pet ( ds_resultado_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
C - Creatinina
G - Glicose
*/
vl_resultado_w 	double precision;
ds_retorno_w	varchar(80);


BEGIN

if (ds_resultado_p IS NOT NULL AND ds_resultado_p::text <> '') then

	vl_resultado_w := somente_numero_exame(ds_resultado_p);

	select	max(ds_resultado)
	into STRICT	ds_retorno_w
	from	hd_valores_pet
	where	ie_tipo_exame = ie_opcao_p
	and	vl_resultado_w between qt_valor_min and qt_valor_max;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
