SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION hd_pac_possui_trat_pend (cd_pessoa_p text) RETURNS varchar AS $body$
DECLARE


			
ie_possui_w		varchar(1);
ie_considera_fim_trat_w varchar(1);			
qt_tratamento_w		bigint;


BEGIN
ie_possui_w := 'S';

ie_considera_fim_trat_w := Obter_param_Usuario(7009, 200, obter_perfil_ativo, obter_usuario_ativo, obter_estabelecimento_ativo, ie_considera_fim_trat_w);

select	count(*)
into STRICT	qt_tratamento_w
from	paciente_tratamento
where	cd_pessoa_fisica = cd_pessoa_p;

if (cd_pessoa_p IS NOT NULL AND cd_pessoa_p::text <> '') and (qt_tratamento_w > 0) and (ie_considera_fim_trat_w = 'S') then
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  ie_possui
	into STRICT	ie_possui_w
	from  	hd_pac_renal_cronico a,
		paciente_tratamento b
	where   a.cd_pessoa_fisica = b.cd_pessoa_fisica
	and	coalesce(b.dt_final_tratamento::text, '') = ''
	and	b.ie_tratamento in ('HD','CH','CHD','CHDF','CHDI','PAF','SCUF','SLED','DPA','CPI','CO','CAPD','PL','DPAV','DDC','IHF','IHDF')
	and	a.cd_pessoa_fisica = cd_pessoa_p
	and	substr(hd_obter_unidade_prc(a.CD_PESSOA_FISICA,'D'),1,255) is not null;
	
end if;

return	ie_possui_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
