SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION hd_permite_dialise_sessao (nr_atendimento_p bigint, nr_seq_dialise_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_tipo_atendimento_w	smallint;
cd_convenio_w		integer;
qt_sessoes_w		bigint;
qt_dialise_w		bigint;
ie_retorno_w		varchar(1);
ie_tipo_convenio_w	smallint;

BEGIN
ie_retorno_w := 'S';
 
 
select	max(ie_tipo_atendimento), 
	max(Obter_Dados_Atendimento(nr_atendimento,'CC')), 
	max(ie_tipo_convenio) 
into STRICT	ie_tipo_atendimento_w, 
	cd_convenio_w, 
	ie_tipo_convenio_w 
from	atendimento_paciente	 
where	nr_atendimento = nr_atendimento_p;
 
select	max(qt_sessoes) 
into STRICT	qt_sessoes_w 
from	hd_regra_sessao 
where	coalesce(ie_tipo_atendimento,ie_tipo_atendimento_w) = ie_tipo_atendimento_w 
and	coalesce(cd_convenio,cd_convenio_w) = cd_convenio_w 
and	coalesce(ie_tipo_convenio,ie_tipo_convenio_w) = ie_tipo_convenio_w 
and	ie_situacao = 'A';
 
select	count(*) 
into STRICT	qt_dialise_w 
from	hd_dialise 
where	nr_atendimento = nr_atendimento_p 
and 	coalesce(dt_cancelamento::text, '') = '' 
and	nr_sequencia <= nr_seq_dialise_p;
 
if (qt_sessoes_w IS NOT NULL AND qt_sessoes_w::text <> '') then 
	if (qt_dialise_w >= qt_sessoes_w) then 
		ie_retorno_w := 'N';
	end if;
end if;
 
return	ie_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
