CREATE OR REPLACE FUNCTION hd_somente_numero_atx ( ds_campo_p text) RETURNS bigint AS $body$
DECLARE


ds_campo_w    	varchar(255);
cd_campo_w    	double precision := 0;
ds_campo_ww	varchar(255);
i     		integer;
BEGIN

ds_campo_ww		:= ds_campo_p;
ds_campo_w		:= '';

if (coalesce(ds_campo_p::text, '') = '') or (ds_campo_p = '') then
	cd_campo_w	:= null;
else
	begin
	select	(ds_campo_ww)::numeric
	into STRICT	cd_campo_w
	;
	exception
		when data_exception then
		begin
		for i in 1..length(ds_campo_p) loop
	 		begin
		 	if (substr(ds_campo_p, i, 1) in ('1','2','3','4','5','6','7','8','9','0',',','.')) then
	  			ds_campo_w := ds_campo_w || substr(ds_campo_p, i, 1);


			end if;
			end;
		end loop;
		if (coalesce(ds_campo_w::text, '') = '') or (ds_campo_w = '') then
			cd_campo_w		:= null;
		else

			if (ds_campo_w like('%,%')) then
				ds_campo_w	:= replace(ds_campo_w,',','X');
				ds_campo_w	:= replace(ds_campo_w,'.','');
				ds_campo_w	:= replace(ds_campo_w,'X','.');
			end if;

			begin
			cd_campo_w := (substr(ds_campo_w,1,38))::numeric;
			exception
			when others then
				ds_campo_w	:= replace(ds_campo_w,'.',',');
				begin
				cd_campo_w := (substr(ds_campo_w,1,38))::numeric;
				exception
				when others then
					CALL wheb_mensagem_pck.exibir_mensagem_abort(' '|| wheb_mensagem_pck.get_texto(794159) ||' '|| ds_campo_p);
				end;
			end;
		end if;
		end;
	end;
end if;

return cd_campo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
