SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION hd_valida_rule_prescricao (CD_PESSOA_FISICA_P text) RETURNS bigint AS $body$
DECLARE

  wCount bigint;

BEGIN
  BEGIN
    SELECT Count(1) INTO STRICT wCount
    FROM cpoe_dialise 
    WHERE	cd_pessoa_fisica = CD_PESSOA_FISICA_P
    AND (dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
    AND coalesce(dt_suspensao::text, '') = ''
    AND (coalesce(dt_fim::text, '') = '' OR dt_fim > clock_timestamp())
    AND ie_tipo_hemodialise = 'CHDI';
  END;
  RETURN wCount;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
