CREATE OR REPLACE FUNCTION hd_verifica_pac_unid_externa (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);


BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from 	hd_pac_renal_cronico b,
		hd_unidade_dialise c
	where	c.nr_sequencia = hd_obter_unidade_prc(b.cd_pessoa_fisica, 'C')
	and	b.cd_pessoa_Fisica = cd_pessoa_fisica_p
	and	c.ie_propria = 'N';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
