CREATE OR REPLACE FUNCTION hemolabor_obter_dados_soluc ( nr_seq_ordem_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);


BEGIN

if (nr_seq_ordem_p IS NOT NULL AND nr_seq_ordem_p::text <> '') and (ie_opcao_p = 'O') then
	select 		max(ds_orientacao)
	into STRICT		ds_retorno_w
	from   		prescr_solucao a,
			prescr_material b
	where		a.nr_prescricao = b.nr_prescricao
	and		a.nr_seq_solucao = b.nr_sequencia_solucao
	and 		b.nr_seq_ordem_prod = nr_seq_ordem_p;

end if;

if (nr_seq_ordem_p IS NOT NULL AND nr_seq_ordem_p::text <> '') and (ie_opcao_p = 'T') then
	select 		max(qt_hora_fase)
	into STRICT		ds_retorno_w
	from   		prescr_solucao a,
			prescr_material b
	where		a.nr_prescricao = b.nr_prescricao
	and			a.nr_seq_solucao = b.nr_sequencia_solucao
	and 		b.nr_seq_ordem_prod = nr_seq_ordem_p;
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
