CREATE OR REPLACE FUNCTION hem_obter_desc_bifurcacao (cd_bifurcacao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN

if (cd_bifurcacao_p IS NOT NULL AND cd_bifurcacao_p::text <> '') then

	select 	ds_valor_dominio
	into STRICT	ds_retorno_w
	from   	valor_dominio_v
	where	coalesce(ie_situacao,'A') = 'A'
	and		cd_dominio = 6161
	and 	vl_dominio = cd_bifurcacao_p;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
