CREATE OR REPLACE FUNCTION hem_obter_dif_perc_result ( ie_parametro_p text, qt_valor_inicial_p bigint, qt_valor_final_p bigint) RETURNS varchar AS $body$
DECLARE


qt_diferenca_w		double precision;
qt_dif_perc_w		double precision;


BEGIN
qt_diferenca_w	:= ABS(qt_valor_inicial_p - qt_valor_final_p);


--if	(ie_parametro_p = 'A') then
	qt_dif_perc_w	:= (qt_diferenca_w*100)/qt_valor_inicial_p;

--elsif	(ie_parametro_p = 'G') then
--end if;
return	qt_dif_perc_w;

end;
