CREATE OR REPLACE FUNCTION hem_obter_dominancia (nr_seq_importancia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_dominancia_w		varchar(255);


BEGIN
if (coalesce(nr_seq_importancia_p,0) > 0) then

	select	max(ds_expressao)
	into STRICT	ds_dominancia_w
	from	hem_importancia_vaso a,
		valor_dominio_v b
	where	a.nr_sequencia	= nr_seq_importancia_p
	and	b.vl_dominio	= a.ie_dominancia
