SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION hem_obter_inf_stent ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE



ds_stent_w                    	varchar(80);
ie_tipo_w                     	varchar(2);
ie_stent_direto_w             	varchar(1);
ie_stent_bioabsorvivel_w      	varchar(1);
nr_seq_stent_farm_w	      	bigint;
ds_retorno_w			varchar(255);
ds_tipo_farmaco_w		varchar(80);
ie_parenteses_w			varchar(1);


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	ie_parenteses_w	:= 'N';

	select 	max(ds_stent),
		max(ie_tipo),
		max(ie_stent_direto),
		max(ie_stent_bioabsorvivel),
		max(nr_seq_stent_farm)
	into STRICT	ds_stent_w,
		ie_tipo_w,
		ie_stent_direto_w,
		ie_stent_bioabsorvivel_w,
		nr_seq_stent_farm_w
	from	hem_stent
	where	nr_sequencia = nr_sequencia_p;

	if (ie_tipo_w = 'D') then

		ie_parenteses_w	:= 'S';

		if (nr_seq_stent_farm_w IS NOT NULL AND nr_seq_stent_farm_w::text <> '') then -- Se for DES trazer a informação do Stent fármaco
			select 	max(ds_tipo)
			into STRICT	ds_tipo_farmaco_w
			from	hem_stent_farmaco
			where	nr_Sequencia = nr_seq_stent_farm_w;

			ds_retorno_w := ds_retorno_w || ' ('||obter_desc_expressao(650656)||', '||ds_tipo_farmaco_w;
		else
			ds_retorno_w := ds_retorno_w || ' ('||obter_desc_expressao(650656)||'';
		end if;
	elsif (ie_tipo_w = 'B') then
		ie_parenteses_w	:= 'S';
		ds_retorno_w	:= ds_retorno_w || ' ('||obter_desc_expressao(650657)||'';
	end if;

	if (ie_stent_direto_w = 'S') then
		ie_parenteses_w	:= 'S';
		ds_retorno_w	:= ds_retorno_w || ', ' || wheb_mensagem_pck.get_texto(795783);
	end if;

	if (ie_stent_bioabsorvivel_w = 'S') then
		ie_parenteses_w	:= 'S';
		ds_retorno_w	:= ds_retorno_w || ', ' || wheb_mensagem_pck.get_texto(795784);
	end if;

	if (ie_parenteses_w = 'S') then
		ds_retorno_w	:= ds_stent_w || ds_retorno_w||')';
	else
		ds_retorno_w	:= ds_stent_w;
	end if;

end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
