SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION hem_obter_panel_syntax ( nr_seq_syntax_p bigint) RETURNS varchar AS $body$
DECLARE


nm_panel_w	varchar(255);
nr_seq_seg_w	bigint;
cd_segmento_w	varchar(3);
ie_dominancia_w	varchar(15);
ie_rca_w	varchar(1);
ie_lad_w	varchar(1);
ie_lcx_w	varchar(1);

C01 CURSOR FOR
	SELECT	b.nr_sequencia,
		b.cd_segmento,
		d.ie_dominancia
	from	hem_syntax_segmento a,
		hem_segmento_syntax b,
		hem_syntax_lesao c,
		hem_syntax d
	where	a.nr_seq_segmento	= b.nr_sequencia
	and	d.nr_sequencia		= c.nr_seq_syntax
	and	c.nr_sequencia		= a.nr_seq_lesao
	and	c.nr_seq_syntax		= nr_seq_syntax_p;


BEGIN
if (coalesce(nr_seq_syntax_p,0) > 0) then

	ie_rca_w := 'N';
	ie_lad_w := 'N';
	ie_lcx_w := 'N';

	open C01;
	loop
	fetch C01 into
		nr_seq_seg_w,
		cd_segmento_w,
		ie_dominancia_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		if (cd_segmento_w in ('1','2','3','4','16','16a','16b','16c')) then
			ie_rca_w := 'S';
		end if;

		if (cd_segmento_w in ('6','7','8','9','9a','10','10a')) then
			ie_lad_w := 'S';
		end if;

		if (cd_segmento_w in ('11','12','12a','12b','13','14','14a','14b','15')) then
			ie_lcx_w := 'S';
		end if;

		if (ie_dominancia_w = 'E') then

			if	((ie_rca_w = 'S') and (ie_lad_w = 'S') and (ie_lcx_w = 'S')) then
				nm_panel_w := 'left_pa';
			end if;
			if	((ie_rca_w = 'S') and (ie_lad_w = 'N') and (ie_lcx_w = 'N')) then
				nm_panel_w := 'rca_left_pa';
			end if;
			if	((ie_rca_w = 'S') and (ie_lad_w = 'N') and (ie_lcx_w = 'S')) then
				nm_panel_w := 'rca_lcx_left_pa';
			end if;
			if	((ie_rca_w = 'S') and (ie_lad_w = 'S') and (ie_lcx_w = 'N')) then
				nm_panel_w := 'rca_lad_left_pa';
			end if;
			if	((ie_rca_w = 'N') and (ie_lad_w = 'S') and (ie_lcx_w = 'S')) then
				nm_panel_w := 'lad_lcx_left_pa';
			end if;
			if	((ie_rca_w = 'N') and (ie_lad_w = 'S') and (ie_lcx_w = 'N')) then
				nm_panel_w := 'lad_left_pa';
			end if;
			if	((ie_rca_w = 'N') and (ie_lad_w = 'N') and (ie_lcx_w = 'S')) then
				nm_panel_w := 'lcx_left_pa';
			end if;
		end if;

		if (ie_dominancia_w = 'D') then
			if	((ie_rca_w = 'S') and (ie_lad_w = 'S') and (ie_lcx_w = 'S')) then
				nm_panel_w := 'right_pa';
			end if;
			if	((ie_rca_w = 'S') and (ie_lad_w = 'N') and (ie_lcx_w = 'N')) then
				nm_panel_w := 'rca_right_pa';
			end if;
			if	((ie_rca_w = 'S') and (ie_lad_w = 'N') and (ie_lcx_w = 'S')) then
				nm_panel_w := 'rca_lcx_right_pa';
			end if;
			if	((ie_rca_w = 'S') and (ie_lad_w = 'S') and (ie_lcx_w = 'N')) then
				nm_panel_w := 'rca_lad_right_pa';
			end if;
			if	((ie_rca_w = 'N') and (ie_lad_w = 'S') and (ie_lcx_w = 'S')) then
				nm_panel_w := 'lad_lcx_right_pa';
			end if;
			if	((ie_rca_w = 'N') and (ie_lad_w = 'S') and (ie_lcx_w = 'N')) then
				nm_panel_w := 'lad_right_pa';
			end if;
			if	((ie_rca_w = 'N') and (ie_lad_w = 'N') and (ie_lcx_w = 'S')) then
				nm_panel_w := 'lcx_right_pa';
			end if;
		end if;
		end;
	end loop;
	close C01;
	if (coalesce(nm_panel_w::text, '') = '') then

		select	CASE WHEN ie_dominancia='E' THEN 'left_pa'  ELSE 'right_pa' END
		into STRICT	nm_panel_w
		from	hem_syntax
		where	nr_sequencia = nr_seq_syntax_p;

	end if;
end if;

return	nm_panel_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
