CREATE OR REPLACE FUNCTION hem_obter_se_permite_lib ( nr_seq_proc_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_permite_w		varchar(1) := 'N';
cd_pessoa_fisica_w	varchar(15);
dt_liberacao_w		timestamp;


BEGIN
if (coalesce(nr_seq_proc_p,0) > 0) then

	select	max(dt_liberacao)
	into STRICT	dt_liberacao_w
	from	hem_proc
	where	nr_sequencia = nr_seq_proc_p;

	if ((nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') and coalesce(dt_liberacao_w::text, '') = '') then

		select	max(cd_pessoa_fisica)
		into STRICT	cd_pessoa_fisica_w
		from	usuario
		where	nm_usuario = nm_usuario_p;

		if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then
			select	coalesce(max(ie_permite_liberar),'N')
			into STRICT	ie_permite_w
			from	hem_profissional
			where	cd_pessoa_fisica = cd_pessoa_fisica_w;
		end if;
	end if;
end if;

return	ie_permite_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
