CREATE OR REPLACE FUNCTION hem_obter_se_seg_contrastado (nr_seq_lesao_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(1);


BEGIN
if (coalesce(nr_seq_lesao_p,0) > 0) then

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	hem_segmento_retrograda a,
		hem_syntax_segmento b
	where	a.nr_seq_syntax = b.nr_seq_segmento
	and	b.nr_seq_lesao = nr_seq_lesao_p
	and	b.ie_start = 'S';

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
