CREATE OR REPLACE FUNCTION hem_obter_status_agenda ( nr_seq_proc_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
IE_OPCAO_P
C = Código
D = Descrição
*/
ds_retorno_w	varchar(255);


BEGIN
if (coalesce(nr_seq_proc_p, 0) > 0) then

	if (ie_opcao_p = 'C') then

		select	CASE WHEN coalesce(dt_inativacao::text, '') = '' THEN CASE WHEN coalesce(dt_liberacao::text, '') = '' THEN CASE WHEN coalesce(dt_digitacao::text, '') = '' THEN 'E'  ELSE 'C' END   ELSE 'L' END   ELSE 'I' END
		into STRICT	ds_retorno_w
		from	hem_proc
		where	nr_sequencia = nr_seq_proc_p;

	elsif (ie_opcao_p = 'D') then

		select	substr(obter_desc_expressao(CASE WHEN coalesce(dt_inativacao::text, '') = '' THEN CASE WHEN coalesce(dt_liberacao::text, '') = '' THEN CASE WHEN coalesce(dt_digitacao::text, '') = '' THEN 491195  ELSE 330733 END   ELSE 292549 END   ELSE 319239 END ),1,255)
		into STRICT	ds_retorno_w
		from	hem_proc
		where	nr_sequencia = nr_seq_proc_p;
	end if;

end if;
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
