SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION hem_obter_status_proced ( nr_seq_cirurgia_p bigint, nr_seq_proc_p bigint, nr_seq_agenda_P bigint) RETURNS varchar AS $body$
DECLARE



nr_atendimento_w 	bigint;
ie_retorno_w		varchar(2);
ie_status_agenda_w	varchar(3);
nr_cirurgia_w		bigint;
dt_inicio_real_w	timestamp;
dt_termino_w		timestamp;
dt_entregue_w		timestamp;
dt_inativacao_w		timestamp;
dt_liberacao_w		timestamp;
dt_alta_w		timestamp;
qtd_w			integer;

/*
E - Entregue
C - Cancelado
LE - Laudo entregue
L - Laudo liberado
AL - Aguardando laudo
P - Em procedimento
AG - Aguardando
A - Agendado
*/
BEGIN

ie_retorno_w	:= 'A';

if (coalesce(nr_seq_proc_p, 0) > 0) then

	select	max(dt_entrega),
		max(dt_inativacao),
		max(dt_liberacao)
	into STRICT	dt_entregue_w,
		dt_inativacao_w,
		dt_liberacao_w
	from	hem_proc
	where	nr_sequencia = nr_seq_proc_p;

	if (dt_inativacao_w IS NOT NULL AND dt_inativacao_w::text <> '') then
		ie_retorno_w	:= 'C';
	elsif (dt_entregue_w IS NOT NULL AND dt_entregue_w::text <> '') then
		ie_retorno_w	:= 'E';
	elsif (dt_liberacao_w IS NOT NULL AND dt_liberacao_w::text <> '') then
		ie_retorno_w	:= 'L';
	elsif (coalesce(dt_liberacao_w::text, '') = '') then
		ie_retorno_w	:= 'AL';
	end if;

elsif (coalesce(nr_seq_cirurgia_p, 0) > 0) then

	select 	max(ie_status_agenda)
	into STRICT	ie_status_agenda_w
	from	agenda_paciente
	where	nr_cirurgia = nr_seq_cirurgia_p;

	select	max(dt_inicio_real),
		max(dt_termino)
	into STRICT	dt_inicio_real_w,
		dt_termino_w
	from	cirurgia
	where	nr_cirurgia = nr_seq_cirurgia_p;

	ie_retorno_w := 'A';

	if (dt_inicio_real_w IS NOT NULL AND dt_inicio_real_w::text <> '') and (coalesce(dt_termino_w::text, '') = '') then
		ie_retorno_w := 'P';
	elsif (ie_status_agenda_w = 'A') or (ie_status_agenda_w = 'AT') then
		ie_retorno_w := 'AG';
	end if;

	if (ie_status_agenda_w = 'C') then
		ie_retorno_w := 'C';
	end if;
else
	select 	max(ie_status_agenda)
	into STRICT	ie_status_agenda_w
	from	agenda_paciente
	where	nr_sequencia = nr_seq_agenda_p;

	if (ie_status_agenda_w = 'A') or (ie_status_agenda_w = 'AT') then
		ie_retorno_w := 'AG';
	end if;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
