SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION hem_obter_syntax_euro (ie_opcao_p text, cd_pessoa_fisica_p text) RETURNS bigint AS $body$
DECLARE

			 
/*Retorno 
S = Syntax Score 
E = EuroScore 
*/
 
cd_syntax_score_w	double precision;
qt_score_euro_w		double precision;
qt_retorno_w		double precision;
nr_seq_syntax_w		bigint;
nr_seq_euro_w		bigint;
nr_atendimento_w	bigint;


BEGIN 
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then 
 
	select	max(a.nr_atendimento) 
	into STRICT	nr_atendimento_w 
	from	atendimento_paciente a, 
		euroscore b 
	where	a.cd_pessoa_fisica = cd_pessoa_fisica_p 
	and	a.nr_atendimento = b.nr_atendimento;
	 
	select	max(nr_sequencia) 
	into STRICT	nr_seq_syntax_w 
	from	hem_syntax 
	where	cd_pessoa_fisica = cd_pessoa_fisica_p;
	 
	select	max(nr_sequencia) 
	into STRICT	nr_seq_euro_w 
	from	euroscore 
	where	nr_atendimento = nr_atendimento_w;
	 
	select	max(cd_syntax_score) 
	into STRICT	cd_syntax_score_w 
	from	hem_syntax 
	where	nr_sequencia = nr_seq_syntax_w;
	 
	select	max(qt_score) 
	into STRICT	qt_score_euro_w 
	from	euroscore 
	where	nr_sequencia = nr_seq_euro_w;
	 
	if (ie_opcao_p = 'S') then 
		qt_retorno_w := cd_syntax_score_w;
	end if;
	if (ie_opcao_p = 'E') then 
		qt_retorno_w := qt_score_euro_w;
	end if;
end if;
 
return	qt_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
