SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION hfp_obter_desc_series (nm_usuario_p text) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(80);
qt_repeticoes_w	varchar(80);

C01 CURSOR FOR 	-- Busca series
	SELECT	qt_repeticoes
	FROM	hfp_serie
	WHERE	ie_situacao = 'A'
	order by nr_sequencia;


BEGIN
ds_retorno_w := '';

open C01;
loop
fetch C01 into
	qt_repeticoes_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (length(ds_retorno_w) > 0) then
		ds_retorno_w := ds_retorno_w || '/';
	end if;
	ds_retorno_w := ds_retorno_w || qt_repeticoes_w;
	end;
end loop;
close C01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
