CREATE OR REPLACE FUNCTION hfp_obter_paciente_presente (nr_seq_paciente_p bigint) RETURNS varchar AS $body$
DECLARE

nr_seq_avaliacao_w	bigint;
qt_acesso_pendente_w	bigint;

BEGIN
qt_acesso_pendente_w := 0;

if (coalesce(nr_seq_paciente_p,0) > 0) then
	begin
	select	max(nr_sequencia)
	into STRICT	nr_seq_avaliacao_w
	from	hfp_cinesio_funcional
	where	nr_seq_paciente = nr_seq_paciente_p
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');

	SELECT	count(nr_sequencia)
	INTO STRICT	qt_acesso_pendente_w
	FROM	hfp_acesso
	WHERE	nr_seq_aval = nr_seq_avaliacao_w
	AND	(dt_entrada IS NOT NULL AND dt_entrada::text <> '')
	AND	coalesce(dt_saida::text, '') = '';

	end;
end if;

if (qt_acesso_pendente_w > 0) then
	return 'S';
else
	return 'N';
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
