SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION hfp_obter_parecer_medico_inf ( nr_seq_paciente_p bigint, ie_tipo_informacao_p text) RETURNS varchar AS $body$
DECLARE

nr_seq_lib_medico_w	bigint;
cd_fase_w		varchar(10);
ds_fase_w		varchar(80);
ie_nivel_risco_w	varchar(1);

BEGIN
/* ie_tipo_informacao_p
FC - Fase Código
FD - Fase Descrição
NR - Nivel Risco
*/
if (coalesce(nr_seq_paciente_p,0) > 0) then
	begin

	SELECT	MAX(nr_sequencia)
	INTO STRICT	nr_seq_lib_medico_w
	FROM	hfp_parecer_medico
	WHERE	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
	AND	nr_seq_paciente = nr_seq_paciente_p;

	SELECT	b.nr_sequencia,
		b.ds_fase,
		a.ie_nivel_risco
	INTO STRICT	cd_fase_w,
		ds_fase_w,
		ie_nivel_risco_w
	FROM	hfp_parecer_medico a,
		hfp_fase b
	WHERE  	a.nr_seq_fase = b.nr_sequencia
	AND	a.nr_sequencia = nr_seq_lib_medico_w;

	end;
end if;

if	ie_tipo_informacao_p = 'FD' then
	return ds_fase_w;
elsif	ie_tipo_informacao_p = 'FC' then
	return cd_fase_w;
elsif	ie_tipo_informacao_p = 'NR' then
	return ie_nivel_risco_w;
else
	return '';
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
