CREATE OR REPLACE FUNCTION hide_psm_mother (NR_ATENDIMENTO_P bigint) RETURNS varchar AS $body$
DECLARE

	DS_MOTHER_W varchar(1);	
	NR_ATENDIMENTO_MAE_W bigint;

BEGIN
SELECT	NR_ATENDIMENTO_MAE INTO STRICT NR_ATENDIMENTO_MAE_W
FROM	ATENDIMENTO_PACIENTE
WHERE	NR_ATENDIMENTO = NR_ATENDIMENTO_P  AND coalesce(DT_CANCELAMENTO::text, '') = '';

IF (coalesce(NR_ATENDIMENTO_MAE_W::text, '') = '') THEN
DS_MOTHER_W := 'Y';
ELSE
DS_MOTHER_W := 'N';
END IF;

RETURN	DS_MOTHER_W;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
