SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION hjf_obter_serie_documento ( nr_seq_protocolo_p bigint, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
cd_serie_documento_w		varchar(4);
ie_tipo_atendimento_w		smallint;
dt_entrega_convenio_w		timestamp;


BEGIN 
 
cd_serie_documento_w:= 'JF';
 
select 	max(ie_tipo_atendimento) 
into STRICT	ie_tipo_atendimento_w 
from 	atendimento_paciente 
where 	nr_atendimento = nr_atendimento_p;
 
if (ie_tipo_atendimento_w = 1) then 
	 
	cd_serie_documento_w:= cd_serie_documento_w || 'I';
 
else 
 
	cd_serie_documento_w:= cd_serie_documento_w || 'E';
 
end if;
 
 
select	coalesce(max(dt_entrega_convenio),clock_timestamp()) 
into STRICT	dt_entrega_convenio_w 
from 	protocolo_convenio 
where 	nr_seq_protocolo = nr_seq_protocolo_p;
 
 
if	(((to_char(dt_entrega_convenio_w,'dd'))::numeric  >= 16) and ((to_char(dt_entrega_convenio_w,'dd'))::numeric  <= 31)) then 
	 
	cd_serie_documento_w:= cd_serie_documento_w || '1';
 
else 
	cd_serie_documento_w:= cd_serie_documento_w || '2';
 
end if;
 
return cd_serie_documento_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION hjf_obter_serie_documento ( nr_seq_protocolo_p bigint, nr_atendimento_p bigint) FROM PUBLIC;

