CREATE OR REPLACE FUNCTION hjf_obter_se_divide_honorario ( cd_procedimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_divide_w		varchar(1):= 'N';


BEGIN

if	((substr(cd_procedimento_p,1,2) in ('31','32','33','34','36')) and (substr(cd_procedimento_p,1,4) not in ('3112','3213'))) then

	ie_divide_w:= 'S';

elsif (substr(cd_procedimento_p,1,4) = '8201') then

	ie_divide_w:= 'S';

else

	ie_divide_w:= 'N';

end if;

return ie_divide_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
