SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION hl7_obter_caminho_segmento ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


nr_sequencia_w		bigint;
ds_descricao_w		varchar(255);
ds_retorno_w		varchar(2000);


BEGIN
nr_sequencia_w := nr_sequencia_p;


while(nr_sequencia_w) > 0 loop
	begin

	begin
	select	b.nr_sequencia,
		'E - ' || a.nm_segmento
	into STRICT	nr_sequencia_w,
		ds_descricao_w
	FROM hl7_segmento a
LEFT OUTER JOIN hl7_atributo b ON (a.nr_sequencia = b.nr_seq_atrib_segm)
WHERE a.nr_sequencia = nr_sequencia_w;
	exception
	when others then
		nr_sequencia_w  := 0;
		ds_descricao_w  := '';
	end;

	if (length(ds_descricao_w) > 0) then
		ds_retorno_w :=   ds_descricao_w || ' --> ' || ds_retorno_w;
	end if;


	begin
	select	b.nr_seq_segmento,
		'A - ' || b.nm_atributo_hl7
	into STRICT	nr_sequencia_w,
		ds_descricao_w
	FROM hl7_segmento a
LEFT OUTER JOIN hl7_atributo b ON (a.nr_sequencia = b.nr_seq_atrib_segm)
WHERE b.nr_sequencia = nr_sequencia_w;
	exception
	when others then
		nr_sequencia_w	:= 0;
		ds_descricao_w  := '';
	end;

	if (length(ds_descricao_w) > 0) then
		ds_retorno_w :=   ds_descricao_w || ' --> ' || ds_retorno_w;
	end if;

	end;
end loop;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
