CREATE OR REPLACE FUNCTION hmcc_dif_entre_datas ( dt_avaliacao_p timestamp, hr_aval_p text, dt_entrada_p timestamp) RETURNS bigint AS $body$
DECLARE


qt_retorno_w	double precision;


BEGIN

begin

qt_retorno_w	:=  abs(trunc((to_date(to_char(dt_avaliacao_p,'dd/mm/yyyy')||' '||hr_aval_p,'dd/mm/yyyy hh24:mi') -  dt_entrada_p) * 24,2));

