CREATE OR REPLACE FUNCTION hmj_obter_pagina_linha ( NR_ROWNUM_P bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w    varchar(100);
nr_auxiliar_w   bigint;

BEGIN

    nr_auxiliar_w := 0;

    FOR i IN 1..10 LOOP

        IF NR_ROWNUM_P between nr_auxiliar_w and nr_auxiliar_w + 10 THEN
           ds_retorno_w := 'p' || i;
           EXIT;
        END IF;

        nr_auxiliar_w := nr_auxiliar_w + 10;

    END LOOP;

return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
