CREATE OR REPLACE FUNCTION hmlb_desc_san_exame_concat ( nr_seq_doacao_p bigint, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1000);
nr_seq_exame_w	bigint;
C01 CURSOR FOR
	SELECT	c.nr_seq_exame
	FROM	san_doacao a,
		san_exame_lote b,
		san_exame_realizado c
	WHERE	a.nr_sequencia = b.nr_seq_doacao
	AND	b.nr_sequencia = c.nr_seq_exame_lote
	AND	a.cd_pessoa_fisica = cd_pessoa_fisica_p
	AND	UPPER(coalesce(c.ds_resultado,c.vl_resultado)) = 'ANDAMENTO'
	AND	((a.nr_sequencia = nr_seq_doacao_p) OR (nr_seq_doacao_p = 0))
	AND	 hmlb_verifica_impressao_csan59(cd_pessoa_fisica_p, nr_seq_doacao_p) = 'N';

BEGIN

open C01;
loop
fetch C01 into
	nr_seq_exame_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ds_retorno_w := ds_retorno_w || substr(obter_desc_san_exame(nr_seq_exame_w),1,200) || ', ';

	end;
end loop;
close C01;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
