SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION hmlb_obter_lote_kit_v2 ( nr_seq_exame_p bigint, nr_seq_lote_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

ds_lote_kit_w	varchar(20) := '';
dt_vencimento_w	timestamp;
ds_kit_w		varchar(255);
ds_resultado_w	varchar(4000);

C01 CURSOR FOR

	SELECT	c.ds_lote_kit,
		to_date(to_char(c.dt_vencimento_lote,'dd/mm/yyyy')),
		substr(san_obter_kit_lote_sorologia(c.nr_kit_lote, coalesce(c.dt_realizado,clock_timestamp()),'D'),1,255) ds_kit
	from	san_doacao a,
		pessoa_fisica b,
		san_exame_realizado c,
		san_exame_lote d,
		san_fim_prod_dia e
	where	a.cd_pessoa_fisica = b.cd_pessoa_fisica
	and	c.nr_seq_exame_lote = d.nr_sequencia
	and	d.nr_seq_doacao = a.nr_sequencia
	and	a.nr_sequencia = e.nr_seq_doacao
	and	c.nr_seq_exame = nr_seq_exame_p
	and	(c.ds_lote_kit IS NOT NULL AND c.ds_lote_kit::text <> '')
	and	e.nr_seq_lote = nr_seq_lote_p
	--and	a.dt_doacao between trunc(decode(dt_dia_p,'0',sysdate,dt_dia_p)) and trunc(decode(dt_dia_p,'0',sysdate,dt_dia_p)) + 86399/86400
	group by c.ds_lote_kit,
		c.dt_vencimento_lote,
		substr(san_obter_kit_lote_sorologia(c.nr_kit_lote, coalesce(c.dt_realizado,clock_timestamp()),'D'),1,255)
	order by c.ds_lote_kit;



BEGIN
open C01;
loop
fetch C01 into	ds_lote_kit_w,
		dt_vencimento_w,
		ds_kit_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (ie_opcao_p = 'L') then
	ds_resultado_w := ds_resultado_w ||ds_lote_kit_w||',';
	elsif (ie_opcao_p = 'V') then
	ds_resultado_w := dt_vencimento_w;
	elsif (ie_opcao_p = 'K') then
	ds_resultado_w := ds_kit_w;
	end if;
	end;
end loop;
close C01;
if (ie_opcao_p = 'L') then
	ds_resultado_w	:= substr(ds_resultado_w, 1, (length(ds_resultado_w) - 1));
end if;

return	ds_resultado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
