CREATE OR REPLACE FUNCTION hmlb_obter_tipagem_sang_prod (nr_seq_exame_abo_p bigint, nr_seq_exame_fator_p bigint, nr_seq_doacao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(10);
ds_abo_w		varchar(5);
ds_fator_rh_w	varchar(5);


BEGIN

if (nr_seq_exame_abo_p IS NOT NULL AND nr_seq_exame_abo_p::text <> '') and (nr_seq_exame_fator_p IS NOT NULL AND nr_seq_exame_fator_p::text <> '') and (nr_seq_doacao_p IS NOT NULL AND nr_seq_doacao_p::text <> '') then

	select	substr(max(x.ds_resultado),1,5)
	into STRICT	ds_abo_w
	from	san_exame_realizado x
	where	x.nr_seq_exame = 	nr_seq_exame_abo_p
	and	x.nr_seq_exame_lote   = 	(SELECT	max(z.nr_sequencia)
					from	san_exame_lote z
					where	z.nr_seq_doacao = nr_seq_doacao_p);


	select	substr(CASE WHEN lower(max(x.ds_resultado))='positivo' THEN '+' WHEN lower(max(x.ds_resultado))='negativo' THEN '-' END ,1,5)
	into STRICT	ds_fator_rh_w
	from	san_exame_realizado x
	where	x.nr_seq_exame =	nr_seq_exame_fator_p
	and	x.nr_seq_exame_lote   = 	(SELECT	max(z.nr_sequencia)
					from	san_exame_lote z
					where	z.nr_seq_doacao = nr_seq_doacao_p);

	ds_retorno_w	:= ds_abo_w||ds_fator_rh_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
