CREATE OR REPLACE FUNCTION horarios_prescr_lib ( nr_prescricao_p bigint, nr_seq_item_p bigint, ie_agrupador_p bigint ) RETURNS varchar AS $body$
DECLARE


ie_itens_liberados_w	varchar(1);


BEGIN

if (ie_agrupador_p <> 5) then

	select	coalesce(max('S'),'N')
	into STRICT	ie_itens_liberados_w
	from	prescr_mat_hor
	where	nr_prescricao = nr_prescricao_p
	and		nr_seq_material	= nr_seq_item_p
	and		ie_agrupador = ie_agrupador_p
	and		(dt_lib_horario IS NOT NULL AND dt_lib_horario::text <> '');

else

	select	coalesce(max('S'),'N')
	into STRICT	ie_itens_liberados_w
	from    prescr_proc_hor b
	where	b.nr_prescricao = nr_prescricao_p
	and	b.nr_seq_procedimento = nr_seq_item_p
	and     (dt_lib_horario IS NOT NULL AND dt_lib_horario::text <> '');


end if;



return ie_itens_liberados_w;

end;
