CREATE OR REPLACE FUNCTION horario_valido (hr_base_p timestamp, hr_inicio_p timestamp, hr_fim_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(001)	:= 'N';
hr_fim_w	timestamp;


BEGIN

ds_retorno_w		:= 'N';
hr_fim_w		:= hr_fim_p;
if (hr_inicio_p > hr_fim_p) then
	hr_fim_w	:= hr_fim_p + 1;
end if;

if (hr_base_p >= hr_inicio_p) and (hr_base_p <= hr_fim_w) then
	ds_retorno_w	:= 'S';
end if;

return	ds_retorno_w;

END;
$body$
