CREATE OR REPLACE FUNCTION hrf_obter_status_ordem (nr_ordem_compra_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(50);
dt_baixa_w			timestamp;
dt_entrega_w			timestamp;
dt_aprovacao_w			timestamp;
dt_envio_email_w		timestamp;
ie_urgente_w			varchar(1);
ie_tipo_ordem_w			varchar(1);
ie_possui_adiantamento_w	varchar(1);
ie_possui_nota_w		varchar(1);
ie_possui_nf_w			varchar(1);


BEGIN

select	dt_baixa,
	dt_entrega,
	dt_aprovacao,
	dt_envio_email,
	ie_urgente,
	ie_tipo_ordem,
	substr(obter_se_ordem_possui_adiant(nr_ordem_compra),1,1) ie_possui_adiantamento,
	substr(CASE WHEN obter_se_ordem_possui_nota(nr_ordem_compra)=0 THEN 'N'  ELSE 'S' END ,1,1) ie_possui_nota,
	substr(CASE WHEN obter_se_oc_vinculada_nf(nr_ordem_compra)=0 THEN 'N'  ELSE 'S' END ,1,1) ie_possui_nf
into STRICT	dt_baixa_w,
	dt_entrega_w,
	dt_aprovacao_w,
	dt_envio_email_w,
	ie_urgente_w,
	ie_tipo_ordem_w,
	ie_possui_adiantamento_w,
	ie_possui_nota_w,
	ie_possui_nf_w
from	ordem_compra
where	nr_ordem_compra = nr_ordem_compra_p;

if (dt_baixa_w IS NOT NULL AND dt_baixa_w::text <> '') then
	ds_retorno_w := 'Baixada';
elsif (ie_urgente_w = 'S') then
	ds_retorno_w := 'Urgente';
elsif (ie_tipo_ordem_w = 'G') then
	ds_retorno_w := 'Cons. Consig.';
elsif (ie_possui_adiantamento_w = 'S') then
	ds_retorno_w := 'Com adiantamento';
elsif (ie_possui_nota_w = 'S') and (coalesce(dt_baixa_w::text, '') = '') then
	ds_retorno_w := 'Parc. Recebida';
elsif (coalesce(dt_baixa_w::text, '') = '') and (dt_entrega_w < clock_timestamp()) then
	ds_retorno_w := 'Atrasada';
elsif (ie_possui_nf_w = 'S') then
	ds_retorno_w := 'OC com NF';
elsif (ie_possui_nota_w = 'S') then
	ds_retorno_w := 'OC com NF calculada';
elsif (dt_aprovacao_w IS NOT NULL AND dt_aprovacao_w::text <> '') and (dt_envio_email_w IS NOT NULL AND dt_envio_email_w::text <> '') then
	ds_retorno_w := 'Aprov./Enviada';
elsif (dt_aprovacao_w IS NOT NULL AND dt_aprovacao_w::text <> '') then
	ds_retorno_w := 'Aprovada';
elsif (coalesce(dt_aprovacao_w::text, '') = '') then
	ds_retorno_w := 'NÃ£o aprovada';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
