CREATE OR REPLACE FUNCTION hrf_obter_status_solic (nr_solic_compra_p bigint) RETURNS varchar AS $body$
DECLARE


dt_liberacao_w		timestamp;
dt_autorizacao_w	timestamp;
dt_baixa_w		timestamp;
nr_seq_motivo_cancel_w	bigint;
ds_retorno_w		varchar(20);
qt_notas_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_notas_w
from (SELECT	a.nr_nota_fiscal nr_nota
	from	nota_fiscal b,
		nota_fiscal_item a
	where	a.nr_sequencia = b.nr_sequencia
	and	a.nr_solic_compra = nr_solic_compra_p
	and	(a.nr_solic_compra IS NOT NULL AND a.nr_solic_compra::text <> '')) alias2;

select	coalesce(dt_liberacao,null),
	coalesce(dt_autorizacao,null),
	coalesce(dt_baixa,null),
	coalesce(nr_seq_motivo_cancel,null)
into STRICT	dt_liberacao_w,
	dt_autorizacao_w,
	dt_baixa_w,
	nr_seq_motivo_cancel_w
from	solic_compra
where	nr_solic_compra = nr_solic_compra_p;

if (qt_notas_w > 0) then
	ds_retorno_w := 'Comprada';
elsif (dt_baixa_w IS NOT NULL AND dt_baixa_w::text <> '') and (coalesce(nr_seq_motivo_cancel_w::text, '') = '') then
	ds_retorno_w := 'Baixada';
elsif (dt_baixa_w IS NOT NULL AND dt_baixa_w::text <> '') and (nr_seq_motivo_cancel_w IS NOT NULL AND nr_seq_motivo_cancel_w::text <> '') then
	ds_retorno_w := 'Cancelada';
elsif (dt_autorizacao_w IS NOT NULL AND dt_autorizacao_w::text <> '') then
	ds_retorno_w := 'Aprovada';
elsif (dt_liberacao_w IS NOT NULL AND dt_liberacao_w::text <> '') then
	ds_retorno_w := 'Liberada';
elsif (coalesce(dt_liberacao_w::text, '') = '') then
	ds_retorno_w := 'NÃ£o Liberada';
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
