CREATE OR REPLACE FUNCTION hrp_obter_faixa_etaria (qt_idade_p bigint) RETURNS varchar AS $body$
DECLARE


ds_idade_w		varchar(50);


BEGIN

	if (qt_idade_p < 21) then
		ds_idade_w	:= '0 - 20';
	elsif (qt_idade_p > 20) and (qt_idade_p < 41) then
		ds_idade_w	:= '21 - 40';
	elsif (qt_idade_p > 40) and (qt_idade_p < 61) then
		ds_idade_w	:= '41 - 60';
	elsif (qt_idade_p > 60) and (qt_idade_p < 81) then
		ds_idade_w	:= '61 - 80';
	elsif (qt_idade_p > 80) then
		ds_idade_w	:= obter_desc_expressao(291569);--'Maior que 80';
	end if;

return	ds_idade_w;

end;
$body$
