SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION hsc_obter_se_alteracao_sv ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1) := 'N';
nr_temp_38_w	bigint := 0;
nr_temp_36_w	bigint := 0;
nr_fr_20_w		bigint := 0;
nr_fc_100_w		bigint := 0;
nr_pa_max_90_w	bigint := 0;
nr_pam_65_w		bigint := 0;
qt_reg_w		bigint := 0;


BEGIN

Select 	count(*)
into STRICT	nr_temp_38_w
from	atendimento_sinal_vital
where	qt_temp > 38
and		nr_sequencia = 	nr_sequencia_p;

Select 	count(*)
into STRICT	nr_temp_36_w
from	atendimento_sinal_vital
where	qt_temp < 36
and		nr_sequencia = 	nr_sequencia_p;

Select 	count(*)
into STRICT	nr_fr_20_w
from	atendimento_sinal_vital
where	qt_freq_resp > 20
and		nr_sequencia = 	nr_sequencia_p;

Select 	count(*)
into STRICT	nr_fc_100_w
from	atendimento_sinal_vital
where	qt_freq_cardiaca > 100
and		nr_sequencia = 	nr_sequencia_p;

Select 	count(*)
into STRICT	nr_pa_max_90_w
from	atendimento_sinal_vital
where	qt_pa_sistolica < 90
and		nr_sequencia = 	nr_sequencia_p;

Select 	count(*)
into STRICT	nr_pam_65_w
from	atendimento_sinal_vital
where	qt_pam < 65
and		nr_sequencia = 	nr_sequencia_p;

qt_reg_w := nr_temp_38_w + nr_temp_36_w + nr_fr_20_w +  nr_fc_100_w + nr_pa_max_90_w + nr_pam_65_w;

If (qt_reg_w) > 1 then

	ds_retorno_w := 'S';

end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
