CREATE OR REPLACE FUNCTION hsc_obter_se_conta_protocolo (nr_seq_proc_p bigint, nr_seq_mat_p bigint) RETURNS bigint AS $body$
DECLARE

 
/* 
 
Function criada para a OS 362624 
 
Esta function permite gerar o repasse somente das contas que enstão em protocolo 
 
retorno: 
	0 - Gera repasse 
	1 - Não gera 
 
*/
 
 
nr_seq_protocolo_w	bigint;
ie_retorno_w		smallint;


BEGIN 
 
select	max(nr_seq_protocolo) 
into STRICT	nr_seq_protocolo_w 
from (SELECT	b.nr_seq_protocolo 
	from	conta_paciente b, 
		procedimento_paciente a 
	where	a.nr_interno_conta	= b.nr_interno_conta 
	and	a.nr_sequencia		= nr_seq_proc_p 
	
union
 
	SELECT	b.nr_seq_protocolo 
	from	conta_paciente b, 
		material_atend_paciente a 
	where	a.nr_interno_conta	= b.nr_interno_conta 
	and	a.nr_sequencia		= nr_seq_mat_p) alias1;
 
ie_retorno_w	:= 1;
 
if (nr_seq_protocolo_w IS NOT NULL AND nr_seq_protocolo_w::text <> '') then 
	ie_retorno_w		:= 0;
end if;
 
return	ie_retorno_w;
 
end;
$body$
