CREATE OR REPLACE FUNCTION hsc_obter_vl_medida_laudo (nr_seq_laudo_p bigint, nr_seq_medida_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w 		varchar(255):='';
ds_valor_medida_w	varchar(255);
qt_medida_w		double precision;
ds_unidade_medida_w	varchar(10);


BEGIN

select 	c.ds_valor_medida,
	c.qt_medida,
	a.ds_unidade_medida
into STRICT	ds_valor_medida_w,
	qt_medida_w,
	ds_unidade_medida_w
from   	medida_exame_laudo a,
	tipo_medida_laudo b,
	laudo_paciente_medida c
where  	a.nr_seq_tipo_medida	= b.nr_sequencia
and    	a.nr_sequencia		= c.nr_seq_medida
and    	c.nr_seq_laudo		= nr_seq_laudo_p
and	c.nr_seq_medida		= nr_seq_medida_p;

if (ds_valor_medida_w IS NOT NULL AND ds_valor_medida_w::text <> '') then
	ds_retorno_w := ds_valor_medida_w;
end if;

if (coalesce(ds_valor_medida_w::text, '') = '') and (qt_medida_w IS NOT NULL AND qt_medida_w::text <> '') then
	ds_retorno_w := qt_medida_w || ' ' || ds_unidade_medida_w;
end if;

if (coalesce(ds_valor_medida_w::text, '') = '') and (coalesce(qt_medida_w::text, '') = '') then
	ds_retorno_w := '';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
