CREATE OR REPLACE FUNCTION hsjb_obter_dados_guia ( nr_atendimento_p bigint, dt_parametro_p timestamp, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255) := '';
cd_senha_w			varchar(20);
nr_doc_convenio_w		varchar(20);

/*	Opção
	S	- Senha
	G	- Guia
*/
c01 CURSOR FOR
	SELECT	/*+ index (a ATECACO_I3) */		cd_senha,
		nr_doc_convenio
	from	Atend_categoria_convenio
	where	dt_parametro_p between dt_inicio_vigencia and coalesce(dt_final_vigencia, clock_timestamp())
	and 	nr_atendimento = nr_atendimento_p
	order by dt_final_vigencia asc;


BEGIN

open c01;
loop
fetch c01 into
	cd_senha_w,
	nr_doc_convenio_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	if (ie_opcao_p = 'S') then
		ds_retorno_w		:= cd_senha_w;
	elsif (ie_opcao_p = 'G') then
		ds_retorno_w		:= nr_doc_convenio_w;
		if (coalesce(ds_retorno_w::text, '') = '') then
			ds_retorno_w:= nr_atendimento_p || '26';
		end if;
	end if;
	end;
end loop;
close c01;

RETURN ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
