SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION hsjb_obter_dietas_concat (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(4000);
ds_retorno_aux_w	varchar(1000);

C01 CURSOR FOR
	SELECT	obter_nome_dieta(a.cd_dieta)
	from	mapa_dieta a
	where	a.nr_sequencia = nr_Sequencia_p
	and		(cd_dieta IS NOT NULL AND cd_dieta::text <> '')
	
union

	SELECT	obter_nome_dieta(a.cd_dieta)
	from	mapa_dieta a
	where	a.nr_seq_superior = nr_Sequencia_p
	and		(cd_dieta IS NOT NULL AND cd_dieta::text <> '')
	order by 1;


BEGIN

open c01;
loop
fetch c01 into ds_retorno_aux_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	ds_retorno_w := ds_retorno_w||', '||ds_retorno_aux_w;

end loop;
close c01;

return	substr(ds_retorno_w,2,3999);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
