CREATE OR REPLACE FUNCTION hsj_tiss_obter_obs_guia (nr_interno_conta_p bigint, nr_seq_guia_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_sangue_w		varchar(20);
ds_sangue_w		varchar(255);
ds_retorno_w		varchar(4000);
			
c01 CURSOR FOR
SELECT	a.nr_sangue,
	b.ds_derivado
from    san_producao a
		join san_derivado b 
                	ON b.nr_sequencia = a.nr_seq_derivado 
		join procedimento_paciente c 
			ON c.nr_seq_transfusao = a.nr_seq_transfusao
where c.nr_interno_conta = nr_interno_conta_p;


BEGIN

ds_retorno_w := '';

open C01;
loop
fetch C01 into	
	nr_seq_sangue_w,
	ds_sangue_w;
EXIT WHEN NOT FOUND; /* apply on C01 */

	ds_retorno_w := ds_retorno_w ||' '||nr_seq_sangue_w||' '|| ds_sangue_w;

end loop;
close C01;

return	substr(ds_retorno_w,1,239);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
