CREATE OR REPLACE FUNCTION hsl_obter_se_proc_pacote ( nr_atendimento_p bigint, nr_seq_proc_pacote_p bigint, cd_setor_exclusivo_p text) RETURNS varchar AS $body$
DECLARE


ie_existe_w		varchar(1);
qt_existe_w		bigint;


BEGIN
select	count(*)
into STRICT	qt_existe_w
from	procedimento_paciente a,
	procedimento b
where	a.cd_procedimento = b.cd_procedimento
and	a.ie_origem_proced = b.ie_origem_proced
and	a.nr_atendimento = nr_atendimento_p
and 	a.nr_Sequencia = nr_Seq_proc_pacote_p
and 	((b.cd_Setor_exclusivo = cd_setor_exclusivo_p) or (cd_setor_exclusivo_p = 0));

ie_existe_w		:= 'N';
if (qt_existe_w > 0) then
	ie_existe_w	:= 'S';
end if;

return ie_existe_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
