CREATE OR REPLACE FUNCTION hsm_obter_saldo_protocolo (nr_Seq_protocolo_p bigint, nr_Seq_retorno_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE

			 
/* 
R	= Valo recursado do protocolo (GRG ou Retorno ConvÃªnio) 
S	= Saldo do protocolo (Valor recursado + Vl Guias sem retorno) 
*/
			 
 
vl_retorno_w		double precision;
vl_recurso_grg_w	double precision;
vl_amenor_w		double precision;
vl_recurso_w		double precision;
vl_conta_sem_ret_w	double precision;


BEGIN 
select	coalesce(sum(obter_valores_guia_grc(a.nr_seq_lote_hist,a.nr_interno_conta,a.cd_autorizacao,'VA')),0) 
into STRICT	vl_recurso_grg_w 
from	lote_audit_hist_guia a, 
	conta_paciente b, 
	protocolo_convenio c 
where	a.nr_interno_conta 	= b.nr_interno_conta 
and	c.nr_seq_protocolo	= b.nr_Seq_protocolo 
and	c.nr_Seq_protocolo	= nr_Seq_protocolo_p 
and	a.nr_Seq_retorno	= nr_Seq_retorno_p;
 
select	coalesce(sum(c.vl_amenor),0) 
into STRICT	vl_amenor_w 
from	convenio_retorno_item c, 
	conta_paciente d 
where	c.nr_interno_conta 	= d.nr_interno_conta 
and	d.nr_Seq_protocolo 	= nr_Seq_protocolo_p;
 
select 	coalesce(sum(obter_valor_conta(a.nr_interno_conta, 0)),0) 
into STRICT	vl_conta_sem_ret_w 
from 	conta_paciente a, 
	protocolo_convenio b 
where 	a.nr_Seq_protocolo 	= b.nr_seq_protocolo 
and	b.nr_Seq_protocolo 	= nr_Seq_protocolo_p 
and	not exists (SELECT	1 
	from 	convenio_retorno_item x 
	where 	x.nr_interno_conta 	= a.nr_interno_conta);
 
--if	(vl_recurso_grg_w > 0) then 
	vl_recurso_w := vl_recurso_grg_w;
--elsif 	(vl_recurso_grg_w <= 0) then 
--	vl_recurso_w := vl_amenor_w; 
--end if; 
 
if (ie_opcao_p = 'R') then 
	vl_retorno_w	:= vl_recurso_w;
elsif (ie_opcao_p = 'S') then 
	vl_retorno_w	:= coalesce(vl_recurso_w,0) + coalesce(vl_conta_sem_ret_w,0);
end if;
 
return vl_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
