SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION hsm_obter_valores_rel_repasse ( nr_repasse_terceiro_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


vl_retorno_w	double precision:=0;

/*
ie_opcao
CD - com devolução
SD - sem devolução
*/
BEGIN

if (coalesce(nr_repasse_terceiro_p,0) > 0) then

	if (coalesce(ie_opcao_p,'X') = 'CD') then
		select	sum(vl_repasse)
		into STRICT	vl_retorno_w
		from 	repasse_terceiro_item
		where 	nr_repasse_terceiro = nr_repasse_terceiro_p
		and 	ds_observacao like '%DEVOLUÇÂO DE IMPOSTOS%';
	elsif (coalesce(ie_opcao_p,'X') = 'SD') then
		select 	sum(vl_repasse)
		into STRICT	vl_retorno_w
		from 	repasse_terceiro_item
		where 	nr_repasse_terceiro = nr_repasse_terceiro_p
		and 	ds_observacao not like '%DEVOLUÇÂO DE IMPOSTOS%';
	end if;

end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
