CREATE OR REPLACE FUNCTION html5_obter_agend_bloq_ag_exam ( cd_estabelecimento_p text, cd_agenda_p text, dt_inicial_p text, dt_final_p text, hr_inicial_p timestamp, hr_final_p timestamp, ie_dia_Semana_p text) RETURNS bigint AS $body$
DECLARE

					
qt_agenda_w			bigint := 0;


BEGIN

if (hr_inicial_p IS NOT NULL AND hr_inicial_p::text <> '') and (hr_final_p IS NOT NULL AND hr_final_p::text <> '') then
	begin
	select	count(*)
	into STRICT	qt_agenda_w
	from	agenda_paciente a,
		agenda b
	where	a.cd_agenda = cd_agenda_p
	and	a.cd_agenda = b.cd_agenda
	and	b.cd_estabelecimento = cd_estabelecimento_p
	and	pkg_date_utils.start_of(a.hr_inicio,'DD',0) between dt_inicial_p and dt_final_p
	and	a.hr_inicio between to_date(to_char(a.hr_inicio, 'dd/mm/yyyy') || ' ' || to_char(hr_inicial_p,'hh24:mi:ss'), 'dd/mm/yyyy hh24:mi:ss')
		and to_date(to_char(a.hr_inicio, 'dd/mm/yyyy') || ' ' || to_char(hr_final_p,'hh24:mi:ss'), 'dd/mm/yyyy hh24:mi:ss')
	and	a.ie_status_agenda not in ('L', 'C')
	and	((pkg_date_utils.get_weekday(a.hr_inicio) = ie_dia_semana_p) or (ie_dia_semana_p = 9 and pkg_date_utils.is_business_day(a.hr_inicio, 0) = 1) or (coalesce(ie_dia_semana_p::text, '') = ''));
	end;
elsif (hr_inicial_p IS NOT NULL AND hr_inicial_p::text <> '') then
	begin
	select	count(*)
	into STRICT	qt_agenda_w
	from	agenda_paciente a,
		agenda b
	where	a.cd_agenda = cd_agenda_p
	and	a.cd_agenda = b.cd_agenda
	and	b.cd_estabelecimento = cd_estabelecimento_p
	and	pkg_date_utils.start_of(a.hr_inicio,'DD',0) between dt_inicial_p and dt_final_p
	and	a.hr_inicio > to_date(to_char(a.hr_inicio, 'dd/mm/yyyy') || ' ' || to_char(hr_inicial_p,'hh24:mi:ss'), 'dd/mm/yyyy hh24:mi:ss')
	and	a.ie_status_agenda not in ('L', 'C')
	and	((pkg_date_utils.get_weekday(a.hr_inicio) = ie_dia_semana_p) or (ie_dia_semana_p = 9 and pkg_date_utils.is_business_day(a.hr_inicio, 0) = 1) or (coalesce(ie_dia_semana_p::text, '') = ''));
	end;
elsif (hr_final_p IS NOT NULL AND hr_final_p::text <> '') then
	begin
	select	count(*)
	into STRICT	qt_agenda_w
	from	agenda_paciente a,
		agenda b
	where	a.cd_agenda = cd_agenda_p
	and	a.cd_agenda = b.cd_agenda
	and	b.cd_estabelecimento = cd_estabelecimento_p
	and	pkg_date_utils.start_of(a.hr_inicio,'DD',0) between dt_inicial_p and dt_final_p
	and	a.hr_inicio < to_date(to_char(a.hr_inicio, 'dd/mm/yyyy') || ' ' || to_char(hr_final_p,'hh24:mi:ss'), 'dd/mm/yyyy hh24:mi:ss')
	and	a.ie_status_agenda not in ('L', 'C')
	and	((pkg_date_utils.get_weekday(a.hr_inicio) = ie_dia_semana_p) or (ie_dia_semana_p = 9 and pkg_date_utils.is_business_day(a.hr_inicio, 0) = 1) or (coalesce(ie_dia_semana_p::text, '') = ''));
	end;
else
	begin
	select	count(*)
	into STRICT	qt_agenda_w
	from	agenda_paciente a,
		agenda b
	where	a.cd_agenda = cd_agenda_p
	and	a.cd_agenda = b.cd_agenda
	and	b.cd_estabelecimento = cd_estabelecimento_p
	and	pkg_date_utils.start_of(a.hr_inicio,'DD',0) between dt_inicial_p and dt_final_p
	and	a.ie_status_agenda not in ('L', 'C')
	and	((pkg_date_utils.get_weekday(a.hr_inicio) = ie_dia_semana_p) or (ie_dia_semana_p = 9 and pkg_date_utils.is_business_day(a.hr_inicio, 0) = 1) or (coalesce(ie_dia_semana_p::text, '') = ''));
	end;
end if;
return qt_agenda_w;
end;
$body$
