SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION html5_obter_status_presc_atend ( nr_atendimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

qt_reg_total_w	bigint;
qt_reg_w	bigint;
ds_retorno_w	varchar(255);



BEGIN

if (ie_opcao_p = 'MED') then

	select	count(*)
	into STRICT	qt_reg_total_w
	from	prescr_mat_hor c,
			prescr_material b,
			prescr_medica a
	where	a.nr_prescricao	= b.nr_prescricao
	and		b.nr_prescricao = c.nr_prescricao
	and		b.nr_sequencia = c.NR_SEQ_MATERIAL
	and		a.nr_atendimento = nr_atendimento_p
	and		(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
	and		coalesce(a.dt_suspensao::text, '') = ''
	and   	coalesce(b.DT_SUSPENSAO::text, '') = ''
	and   	coalesce(c.DT_SUSPENSAO::text, '') = '';

	if (qt_reg_total_w	> 0) then

		select	count(*)
		into STRICT	qt_reg_w
		from	prescr_mat_hor c,
				prescr_material b,
				prescr_medica a
		where	a.nr_prescricao	= b.nr_prescricao
		and		b.nr_prescricao = c.nr_prescricao
		and		b.nr_sequencia = c.NR_SEQ_MATERIAL
		and		a.nr_atendimento = nr_atendimento_p
		and		(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
		and		coalesce(a.dt_suspensao::text, '') = ''
		and   	coalesce(b.DT_SUSPENSAO::text, '') = ''
		and   	coalesce(c.DT_SUSPENSAO::text, '') = ''
		and		(c.dt_fim_horario IS NOT NULL AND c.dt_fim_horario::text <> '');


		return qt_reg_w ||'/'||qt_reg_total_w;

	end if;

elsif (ie_opcao_p = 'LAB') then

	select  count(*)
	into STRICT	qt_reg_total_w
	from  	prescr_procedimento a,
			prescr_medica b
	where 	a.nr_prescricao = b.nr_prescricao
	and		(b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '')
	and		coalesce(a.dt_suspensao::text, '') = ''
	and   	coalesce(b.DT_SUSPENSAO::text, '') = ''
	and   	b.nr_atendimento = nr_atendimento_p
	and   	(a.NR_SEQ_EXAME IS NOT NULL AND a.NR_SEQ_EXAME::text <> '');



	if (qt_reg_total_w	> 0) then

		select  count(*)
		into STRICT	qt_reg_w
		from  	prescr_procedimento a,
				prescr_medica b
		where 	a.nr_prescricao = b.nr_prescricao
		and		(b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '')
		and		coalesce(a.dt_suspensao::text, '') = ''
		and   	coalesce(b.DT_SUSPENSAO::text, '') = ''
		and   	b.nr_atendimento = nr_atendimento_p
		and   	(a.NR_SEQ_EXAME IS NOT NULL AND a.NR_SEQ_EXAME::text <> '')
		and		a.IE_STATUS_ATEND	>= 20;

		return qt_reg_w ||'/'||qt_reg_total_w;

	end if;

elsif (ie_opcao_p = 'IMG') then

	select  count(*)
	into STRICT	qt_reg_total_w
	from 	procedimento c,
			prescr_procedimento a,
			prescr_medica b
	where 	a.nr_prescricao = b.nr_prescricao
	and   	a.cd_procedimento = c.cd_procedimento
	and   	a.ie_origem_proced = c.ie_origem_proced
	and		(b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '')
	and		coalesce(a.dt_suspensao::text, '') = ''
	and   	coalesce(b.DT_SUSPENSAO::text, '') = ''
	and   	b.nr_atendimento = nr_atendimento_p
	and   	coalesce(a.nr_seq_exame::text, '') = ''
	and   	c.CD_TIPO_PROCEDIMENTO in (1,2,3,4,5,8,12,13,15,25,26,30,92,97,104,110,121);




	if (qt_reg_total_w	> 0) then

		select  count(*)
		into STRICT	qt_reg_w
		from 	procedimento c,
				prescr_procedimento a,
				prescr_medica b
		where 	a.nr_prescricao = b.nr_prescricao
		and   	a.cd_procedimento = c.cd_procedimento
		and   	a.ie_origem_proced = c.ie_origem_proced
		and		(b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '')
		and		coalesce(a.dt_suspensao::text, '') = ''
		and   	coalesce(b.DT_SUSPENSAO::text, '') = ''
		and   	b.nr_atendimento = nr_atendimento_p
		and   	coalesce(a.nr_seq_exame::text, '') = ''
		and   	c.CD_TIPO_PROCEDIMENTO in (1,2,3,4,5,8,12,13,15,25,26,30,92,97,104,110,121)
		and		somente_numero(a.IE_STATUS_EXECUCAO) >= 20;

		return qt_reg_w ||'/'||qt_reg_total_w;

	end if;

elsif (ie_opcao_p = 'PROC') then

	select  count(*)
	into STRICT	qt_reg_total_w
	from 	procedimento c,
			prescr_procedimento a,
			prescr_medica b
	where 	a.nr_prescricao = b.nr_prescricao
	and   	a.cd_procedimento = c.cd_procedimento
	and   	a.ie_origem_proced = c.ie_origem_proced
	and		(b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '')
	and		coalesce(a.dt_suspensao::text, '') = ''
	and   	coalesce(b.DT_SUSPENSAO::text, '') = ''
	and   	b.nr_atendimento = nr_atendimento_p
	and   	coalesce(a.nr_seq_exame::text, '') = ''
	and   	coalesce(c.CD_TIPO_PROCEDIMENTO,0) not in (1,2,3,4,5,8,12,13,15,25,26,30,92,97,104,110,121);




	if (qt_reg_total_w	> 0) then

		select  count(*)
		into STRICT	qt_reg_w
		from 	procedimento c,
				prescr_procedimento a,
				prescr_medica b
		where 	a.nr_prescricao = b.nr_prescricao
		and   	a.cd_procedimento = c.cd_procedimento
		and   	a.ie_origem_proced = c.ie_origem_proced
		and		(b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '')
		and		coalesce(a.dt_suspensao::text, '') = ''
		and   	coalesce(b.DT_SUSPENSAO::text, '') = ''
		and   	b.nr_atendimento = nr_atendimento_p
		and   	coalesce(a.nr_seq_exame::text, '') = ''
		and   	coalesce(c.CD_TIPO_PROCEDIMENTO,0) not in (1,2,3,4,5,8,12,13,15,25,26,30,92,97,104,110,121)
		and		(a.DT_BAIXA IS NOT NULL AND a.DT_BAIXA::text <> '');

		return qt_reg_w ||'/'||qt_reg_total_w;

	end if;


end if;



return	null;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
